import React from 'react';
import about from '../img/about1.JPG';

function ImageSection() {
    function DownloadFile(fileName) {
        //Set the File URL.
        var url = "Files/" + fileName;

        //Create XMLHTTP Request.
        var req = new XMLHttpRequest();
        req.open("GET", url, true);
        req.responseType = "blob";
        req.onload = function () {
            //Convert the Byte Data to BLOB object.
            var blob = new Blob([req.response], { type: "application/octetstream" });

            //Check the Browser type and download the File.
            var isIE = false || !!document.documentMode;
            if (isIE) {
                window.navigator.msSaveBlob(blob, fileName);
            } else {
                var url = window.URL || window.webkitURL;
                let link = url.createObjectURL(blob);
                var a = document.createElement("a");
                a.setAttribute("download", fileName);
                a.setAttribute("href", link);
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
            }
        };
        req.send();
    };
    return (
        <div className="ImageSection">
            <div className="img">
                <img src={about} alt=""/>
            </div>
            <div className="about-info">
                <h4>I am<span> Vrudit Patel</span></h4>
                <p className="about-text">
                    I consider my self as a person who loves to acquire knowledge.
                    As a result, I keep my self updated with cutting-edge technologies.
                    
                </p>
                <div className="about-details">
                    <div className="left-section">
                        <p>Full Name</p>
                        <p>Age</p>
                        <p>Nationality</p>
                        <p>Languages</p>
                        <p>Address</p>
                        
                    </div>
                    <div className="right-section">
                        <p>: Vrudit Patel</p>
                        <p>: 21</p>
                        <p>: Indian</p>
                        <p>: English, Hindi, Gujarati</p>
                        <p>: C-101 Kalarav, New Shahibaug, Ahmedabad, GJ, India</p>
                        
                    </div>
                </div>
                <button className="btn" onClick="Download('about2.png')">Download Cv</button>

                {/* <a href="/files/about2.png" download className="res">
                    <button className="btn">Download Cv</button>
                </a> */}
            </div>
        </div>
    )
}

export default ImageSection;
